import React, { useEffect, useRef } from "react";
import Flicking from "@egjs/react-flicking";
import "@egjs/react-flicking/dist/flicking.css";

function Slider() {
  const flickingRef = useRef<Flicking | null>(null);

  useEffect(() => {
    const checkAndInitFlicking = () => {
      if (window.innerWidth <= 768) {
        // If the screen width is 768px or below, and Flicking is not initialized
        if (flickingRef.current && !flickingRef.current.initialized) {
          flickingRef.current.init();
        }
      } else {
        // If the screen width is larger than 768px and Flicking is initialized, you can destroy it if needed
        if (flickingRef.current && flickingRef.current.initialized) {
          flickingRef.current.destroy();
        }
      }
    };

    // Initial check when the component mounts
    checkAndInitFlicking();

    // Listen for window resize events and update Flicking accordingly
    window.addEventListener("resize", checkAndInitFlicking);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener("resize", checkAndInitFlicking);
    };
  }, []);

  return (
    <div className="slider">
      <Flicking
        ref={flickingRef}
        className="slider-viewport"
        useResizeObserver={true}
        align="prev"
        panelsPerView={3}
        circular={false}
        noPanelStyleOverride={false}
        onMoveEnd={(e) => {
          console.log(e);
        }}
      >
        <div className="panel">
          <p>
            “FlavourFinder ignited my culinary journey! Learning to cook online
            is a flavourful adventure.”
          </p>
        </div>
        <div className="panel">
          <p>
            "FlavourFinder made me a kitchen pro. Their online cooking platform
            is practical and empowering!"
          </p>
        </div>
        <div className="panel">
          <p>
            “FlavourFinder honed my kitchen techniques. Their digital cooking
            platform is efficient and engaging!”
          </p>
        </div>
      </Flicking>
    </div>
  );
}

export default Slider;
