import React, { useEffect, useRef, useState } from "react";
import Flicking from "@egjs/react-flicking";
import "@egjs/react-flicking/dist/flicking.css";

function Slider() {
  const flickingRef = useRef<Flicking | null>(null);
  const [columns, setColumns] = useState(3);

  useEffect(() => {
    const checkScreenSize = () => {
      if (window.innerWidth <= 800) {
        // Set columns to 2 for smaller screens
        setColumns(2);

        // Check if Flicking is initialized and call init() if necessary
        if (flickingRef.current && !flickingRef.current.initialized) {
          flickingRef.current.init();
        }
      } else {
        // Set columns to 3 for larger screens
        setColumns(3);

        // Check if Flicking is initialized and call destroy() if necessary
        if (flickingRef.current && flickingRef.current.initialized) {
          flickingRef.current.destroy();
        }
      }
    };

    // Initial check when the component mounts
    checkScreenSize();

    // Listen for window resize events and update columns accordingly
    window.addEventListener("resize", checkScreenSize);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener("resize", checkScreenSize);
    };
  }, []);

  return (
    <div className={`slider columns-${columns}`}>
      <Flicking
        ref={flickingRef}
        autoResize={true}
        useFractionalSize={true}
        noPanelStyleOverride={true}
        className="flicking-viewport"
        align="prev"
        panelsPerView={columns}
        circular={false}
        autoInit={false}
        bou
        onMoveEnd={(e) => {
          console.log(e);
        }}
      >
        <div className="panel">
          <p>
            “FlavourFinder ignited my culinary journey! Learning to cook online
            is a flavourful adventure.”
          </p>
        </div>
        <div className="panel">
          <p>
            "FlavourFinder made me a kitchen pro. Their online cooking platform
            is practical and empowering!"
          </p>
        </div>
        <div className="panel">
          <p>
            “FlavourFinder honed my kitchen techniques. Their digital cooking
            platform is efficient and engaging!”
          </p>
        </div>
      </Flicking>
    </div>
  );
}

export default Slider;
