import React, { useEffect, useRef } from "react";
import A11YSlider from "a11y-slider";

function Slider() {
  const sliderRef = useRef<HTMLUListElement | null>(null);

  useEffect(() => {
    if (sliderRef.current) {
      const slider = new A11YSlider(sliderRef.current, {
        adaptiveHeight: true,
        dots: false,
      });

      return () => {
        slider.destroy();
      };
    }
  }, []);

  return (
    <ul ref={sliderRef} className="slider">
      <li>1</li>
      <li>2</li>
      <li>3</li>
      <li>4</li>
    </ul>
  );
}

export default Slider;
