import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { UseAuthContext } from '../hooks/useAuthContext';
import { Recipe } from '../components/HomeRecipes';

function IndividualRecipe() {
  const { user } = UseAuthContext();
  const { id } = useParams<{ id?: string }>(); // Make the 'id' parameter optional
  const [recipe, setRecipe] = useState<Recipe | null>(null);

  // Define a configuration object for the API routes
  const apiConfig = {
    recipeRoute: '/api/recipes', // Adjust as needed
    userRecipeRoute: '/api/UserRecipes', // Adjust as needed
  };

  useEffect(() => {
    const fetchData = async () => {
      if (id) {
        const routeToUse = user.isAdmin ? apiConfig.userRecipeRoute : apiConfig.recipeRoute;

        try {
          const response = await fetch(`${routeToUse}/${id}`, {
            headers: { Authorization: `Bearer ${user.token}` }
          });

          if (response.ok) {
            const data: Recipe = await response.json();
            setRecipe(data);
          } else {
            // Handle error when the recipe is not found
          }
        } catch (error) {
          // Handle any fetch errors
        }
      }
    };

    fetchData();
  }, [id, user.isAdmin]);

  if (!recipe) {
    // Render a loading indicator or an error message here
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{recipe.title}</h1>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>
            {ingredient.name}: {ingredient.quantity} {ingredient.unit}
          </li>
        ))}
        <img src={recipe.img} alt={recipe.img} />
      </ul>
    </div>
  );
}

export default IndividualRecipe;
