// App.tsx
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavbarComponent from "./components/navbar";
import HomePage from "./pages/homePage";
import SignUpComponent from "./pages/SignUp";
import SavedRecipesComponent from "./pages/dashboard/SavedRecipesComponent";
import UserDashboardHome from "./pages/dashboard/userDashboard";
import SideBarComponent from "./components/SideBarComponent";

function App() {
  // A helper function to determine if the navbar should be shown
  const shouldShowNavbar = (pathname: string) => {
    return pathname !== "/signup" && pathname !== "/login";
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <>
              {shouldShowNavbar(window.location.pathname) && (
                <NavbarComponent />
              )}
              <HomePage />
            </>
          }
        />
        <Route path="/signup" element={<SignUpComponent />} />

        <Route path="/userDashboard" element={<UserDashboardHome />} />
        <SideBarComponent>
        <Route
          path="/userDashboard/:id"
          element={<SavedRecipesComponent />}
        ></Route>
        </SideBarComponent>
      </Routes>
    </Router>
  );
}

export default App;
