// SavedRecipesComponent.tsx
import React from "react";
import { useEffect, useState } from "react";
import { Recipe as UserSavedRecipes } from "../../components/HomeRecipes";
function SavedRecipesComponent() {
  useEffect(() => {
    console.log("saved recipes rendered");
  }, []);

  const [Savedrecipes, setSavedRecipes] = useState<UserSavedRecipes[] | null>(
    null
  );

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch("/api/userRecipes");
        if (!response.ok) {
          console.error(
            `Failed to fetch user saved recipes Status: ${response.status}`
          );
          return;
        }
        const json = await response.json();
        setSavedRecipes(json);
        console.log(json, "user Saved Recipes");
      } catch (error) {
        console.error("Error during fetch:", error);
      }
    };

    fetchRecipes();
  }, []);

  return (
    <div>
   {recipes &&
        recipes.map((recipe) => (
          <div key={recipe._id}>
            <img src={recipe.img} alt="food-img"></img>
            <p>{recipe.title}</p>
            <button
              className="save-recipe-btn"
              onClick={() => saveUserRecipes(recipe._id)}
            >
              Save
            </button>
          </div>
        ))}
    </div>
  );
}

export default SavedRecipesComponent;
