import React from "react";
import { UseAuthContext } from "./useAuthContext";

function useLogout() {
  const { dispatch } = UseAuthContext();
  const logout = () => {
    localStorage.removeItem("user");

    dispatch({ type: "LOGUT" });
  };

  //remove user from storage

  //export logout fumction
  return { logout };
}

export default useLogout;
