import React, { useEffect, useState, useRef } from "react";
import { Recipe as UserSavedRecipes } from "../../components/HomeRecipes";
import { UseSavedRecipesContext } from "../../hooks/useSavedRecipes";

function SavedRecipesComponent() {
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const timeoutRef = useRef<number | null>(null);

  const { savedRecipes , dispatch } = UseSavedRecipesContext();
  useEffect(() => {
    console.log("saved recipes rendered");
  }, []);

  // const [savedRecipes, setSavedRecipes] = useState<UserSavedRecipes[] | null>(
  //   null
  // );

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch("/api/userRecipes");
        if (!response.ok) {
          console.error(
            `Failed to fetch user saved recipes Status: ${response.status}`
          );
          return;
        }
        const json = await response.json();
        setSavedRecipes(json);
        console.log(json, "user Saved Recipes");
      } catch (error) {
        console.error("Error during fetch:", error);
      }
    };

    fetchRecipes();
  }, []);

  const deleteSavedRecipe = async (savedRecipeId: any) => {
    console.log("Deleting saved recipe with ID:", savedRecipeId);

    try {
      const response = await fetch("/api/userRecipes/" + savedRecipeId, {
        method: "DELETE",
      });

      const json = await response.json();

      if (response.ok) {
        dispatch({ type: "DELETE_SAVED_RECIPE", payload: json });
      }

      // Add your delete logic here
      // For example, you can use the savedRecipeId to send a DELETE request to your API

      if (!response.ok) {
        setError("Failed to delete recipe");
        console.error(
          `Failed to delete recipe with ID ${savedRecipeId}. Status: ${response.status}`
        );
        return;
      }

      setSuccess("Recipe deleted successfully");

      // Reset success message after 800 milliseconds (0.8 seconds)
      timeoutRef.current = window.setTimeout(() => {
        setSuccess("");
        setError("");
      }, 800);
    } catch (error) {
      setError("Error during delete");
      console.error("Error during delete:", error);
    }
  };

  return (
    <div>
      {savedRecipes &&
        savedRecipes.map((savedRecipe) => (
          <div key={savedRecipe._id}>
            <img src={savedRecipe.img} alt="food-img"></img>
            <p>{savedRecipe.title}</p>
            <button
              onClick={() => deleteSavedRecipe(savedRecipe._id)}
              className="save-recipe-btn"
            >
              Delete
            </button>
          </div>
        ))}
      <span className={error !== "" ? "error-message" : ""}>{error}</span>
      <span className={success !== "" ? "success-message" : ""}>{success}</span>
    </div>
  );
}

export default SavedRecipesComponent;
