import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

// Importing translation files
import translationEN from "../src/languages/english.json";
import translationES from "../src/languages/spanish.json"; // Import the Spanish translation file

// Creating object with the variables of imported translation files
const resources = {
  en: {
    translation: translationEN,
  },
  es: { // Use "es" for Spanish
    translation: {
 sidebar: {
    savedRecipes
 }
    },
  },
};

// i18N Initialization
i18n
  .use(initReactI18next)
  .init({
    resources,
    lng: "en", // Default language
    keySeparator: false,
    interpolation: {
      escapeValue: false,
    },
  });

export default i18n;
