import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { UseAuthContext } from '../hooks/useAuthContext';
import { Recipe } from '../components/HomeRecipes';

function IndividualRecipe() {
  const { user } = UseAuthContext();
  const { id } = useParams<{ id?: string }>();
  const [recipe, setRecipe] = useState<Recipe | null>(null);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    if (id) {
      const fetchRecipeData = async () => {
        try {
          const response = await fetch(`/api/recipes/${id}`, {
            headers: { Authorization: `Bearer ${user.token}` },
          });
          if (response.ok) {
            const data: Recipe = await response.json();
            setRecipe(data);
          } else {
            // Handle error when the recipe is not found
          }
        } catch (error) {
          // Handle any fetch errors
        } finally {
          // Set loading state to false when the request is complete
          setLoading(false);
        }
      };

      fetchRecipeData();
    }
  }, [id, user.token]);

  if (loading) {
    // Display a loading indicator
    return <div>Loading...</div>;
  }

  if (!recipe) {
    // Handle error or display a message when the recipe is not found
    return <div>Recipe not found.</div>;
  }

  return (
    <div>
      <h1>{recipe.title}</h1>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>
            {ingredient.name}: {ingredient.quantity} {ingredient.unit}
          </li>
        ))}
        <img src={recipe.img} alt={recipe.img} />
      </ul>
    </div>
  );
}

export default IndividualRecipe;
