import React from "react";
import { BrowserRouter as Router, Route, Routes, useParams } from "react-router-dom";
import NavbarComponent from "./components/navbar";
import HomePage from "./pages/homePage";
import SignUpComponent from "./pages/SignUp";
import SavedRecipesComponent from "./pages/dashboard/SavedRecipesComponent";
import UserDashboardHome from "./pages/dashboard/userDashboard";
import HomeRecipes from "./components/HomeRecipes";

function App() {
  // A helper function to determine if the navbar should be shown
  const shouldShowNavbar = (pathname: string) => {
    return pathname !== "/signup" && pathname !== "/login";
  };

  return (
    <Router>
      {shouldShowNavbar(window.location.pathname) && <NavbarComponent />}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/signup" element={<SignUpComponent />} />
        <Route
          path="/userDashboard/:id"
          element={<DashboardWithRecipes />}
        />
      </Routes>
    </Router>
  );
}

function DashboardWithRecipes() {
  const { id } = useParams();

  // Check if id exists or meets some condition
  const shouldRenderHomeRecipes = id && id.length > 0; // You can customize this condition

  return (
    <UserDashboardHome>
      {shouldRenderHomeRecipes && <HomeRecipes />}
      <SavedRecipesComponent />
    </UserDashboardHome>
  );
}

export default App;
