import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { UseAuthContext } from '../hooks/useAuthContext';
import { Recipe } from '../components/HomeRecipes';

function IndividualRecipe() {


    const {user} = UseAuthContext();
  const { id } = useParams<{ id: string }>(); // Get the 'id' parameter from the URL
  const [recipe, setRecipe] = useState<Recipe | null>(null);

  useEffect(() => {
    // Fetch the recipe data based on the 'id' parameter
    const fetchRecipeData = async () => {
      try {
        const response = await fetch(`/api/recipes/${id}`,  {
            headers: { Authorization: `Bearer ${user.token}` }
        }); // Adjust the API endpoint to match your backend
        if (response.ok) {
          const data: Recipe = await response.json();
          setRecipe(data);
          console.log(recipe)
        } else {
          // Handle error when the recipe is not found
        }
      } catch (error) {
        // Handle any fetch errors
      }
    };

    fetchRecipeData();
  }, [id]);

  if (!recipe) {
    // Render a loading indicator or an error message here
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{recipe.title}</h1>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient.name}: {ingredient.quantity} {ingredient.unit}</li>
        ))}
      </ul>
    </div>
  );
}

export default IndividualRecipe;
