import React, { useEffect, useRef } from "react";
import Flicking from "@egjs/react-flicking";
import "@egjs/react-flicking/dist/flicking.css";
import TestimonialCircleOneLarge from "../images/testimonial-circle-1-large.png"
import TestimonialCircleTwoLarge from "../images/testimonial-circle-1-large.png"
import TestimonialCircleThreeLarge from "../images/testimonial-circle-1-large.png"

function Slider() {
  const flickingRef = useRef<Flicking | null>(null);

  useEffect(() => {
    const checkScreenSize = () => {
      if (window.innerWidth <= 800) {
        flickingRef.current?.init();
      } else {
        flickingRef.current?.destroy();
      }
    };

    // Initial check when the component mounts
    checkScreenSize();

    // Listen for window resize events and update Flicking accordingly
    window.addEventListener("resize", checkScreenSize);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener("resize", checkScreenSize);
    };
  }, []);

  return (
    <div className="slider">
      <Flicking
        cam
        ref={flickingRef}
        className="flicking-viewport"
        align="prev"
        circular={true}
        autoInit={false}
        circularFallback={"bound"}
        onMoveEnd={(e) => {
          console.log(e);
        }}
      >
        <div className="panel">
          <p>
            “FlavourFinder ignited my culinary journey! Learning to cook online
            is a flavourful adventure.”
          </p>
        </div>
        <div className="panel">
          <p>
            "FlavourFinder made me a kitchen pro. Their online cooking platform
            is practical and empowering!"
          </p>
        </div>
        <div className="panel">
          <p>
            “FlavourFinder honed my kitchen techniques. Their digital cooking
            platform is efficient and engaging!”
          </p>
        </div>
      </Flicking>
    </div>
  );
}

export default Slider;
