  const startCountingRecipieStat = () => {
    let i = 0;
    const interval = setInterval(() => {
      if (i <= RecipieStat) {
        setCurrentRecipieIteration(i);
        i += 100; // Adjust the increment as needed
      } else {
        clearInterval(interval);
      }
    }, 50); // You can adjust the interval speed if needed
  };


