  return (
    <main>
      <section className="pbl-4">
        <div className="container">
          <img
            className="grapes-decoration pos-absolute"
            alt="grapes-decoration"
            src={grapesDecoration}
          ></img>
          <div className="even-columns">
            <div>
              <div className="flow">
                <h1>
                  Wake Up To The <span> Flavour Revolution</span>
                </h1>
                <p className="pbl-4">
                  Join a vibrant community of food enthusiasts on{" "}
                  <span> Flavour Finder, the recipe website that brings </span>{" "}
                  culinary creativity to life!
                </p>
                <div className="cta-buttons pb-5">
                  <button className="curved-btn ">Join Up</button>
                  <div className="play-btn-container">
                    <button className="play-btn">
                      <PlayIcon />
                    </button>
                    <p>How It Works</p>
                  </div>
                </div>
                <div className="display-f">
                  <div className="stats-hero-section mr-2">
                    <div className="even-columns">
                      <div
                        className="display-f align-start mr-3"
                        id="users-info-column"
                      >
                        <div className="icon-circle">
                          <UserIcon />
                        </div>
                        <div className="ml-2">
                          <h3>{formatIteration(currentUserIteration)}</h3>
                          <p>Happy Users</p>
                        </div>
                      </div>
                      <div
                        className="display-f align-start"
                        id="users-info-column"
                      >
                        <div className="icon-circle">
                          <Burger />
                        </div>
                        <div className="ml-2">
                          <h3>{currentRecipieIteration}</h3>
                          <p>Recipies</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div id="hero-graphic-column">
              {" "}
              <img src={heroImg} alt="hero-img"></img>
            </div>
          </div>
        </div>
        <div className="scroll-indicator-container">
          <ScrollIndicator
            onMouseEnter={handleHover}
            onMouseLeave={handleLeave}
          />
          <p>Scroll Down</p>
          <DownArrow isHovered={isArrowHovered} />
        </div>
      </section>
    </main>
  );
}

export default HomePage;
