import React from "react";
import {
  BrowserRouter as Router,
  Route,
  createBrowserRouter,
  createRoutesFromElements,
  RouterProvider,
  Navigate
} from "react-router-dom";
import NavbarComponent from "./components/navbar";
import HomePage from "./pages/homePage";
import SignUpComponent from "./pages/SignUp";
import UserDashboardHome from "./pages/dashboard/userDashboard";
import SavedRecipesComponent from "./pages/dashboard/SavedRecipesComponent";
import HomeRecipes from "./components/HomeRecipes";
import UserFriendsComponent from "./components/UserFriendsComponent";
import LogInComponent from "./pages/login";
import { UseAuthContext } from "./hooks/useAuthContext";

function App() {
  const { dispatch, user } = UseAuthContext();

  console.log("AuthContext State:", user);

  const shouldShowNavbar = (pathname: string) => {
    return pathname === "/";
  };

  const router = createBrowserRouter(
    createRoutesFromElements(
      shouldShowNavbar(window.location.pathname) && <NavbarComponent />,
      <Route>
        <Route
          index
          element={user ? <Navigate to={"/userDashboard"} /> : <HomePage />}
        />
        <Route
          path="/signup"
          element={user ? <Navigate to={"/userDashboard"} /> : <SignUpComponent />}
        />
        <Route
          path="/login"
          element={user ? <Navigate to={"/userDashboard"} /> : <LogInComponent />}
        />

        <Route
          path="/userDashboard/*"
          element={user ? <UserDashboardHome /> : <Navigate to={"/"} />}
        >
          <Route
            index
            element={
              user ? (
                <HomeRecipes />
              ) : (
                <Navigate to={"/login"} />
              )
            }
          />
          <Route
            path="savedRecipes"
            element={
              user ? (
                <SavedRecipesComponent />
              ) : (
                <Navigate to={"/login"} />
              )
            }
          />
          <Route
            path="friends"
            element={
              user ? (
                <>
                  <UserFriendsComponent />
                </>
              ) : (
                <Navigate to={"/login"} />
              )
            }
          />
        </Route>
      </Route>
    )
  );

  return <RouterProvider router={router} />;
}

export default App;
