import { UseAuthContext } from "./useAuthContext";
import { useState } from "react";
const { jwt } = require("../co")

export const useLogin = () => {
  const [error, setError] = useState<any>(null);
  const [isLoading, setIsLoading] = useState<any>(null);

  // Context
  const { dispatch } = UseAuthContext();

  const login = async (email: any, password: any) => {
    setIsLoading(true);
    setError(null);

    const data = { email, password };

    const response = await fetch("/api/user/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    });

    if (!response.ok) {
      const json = await response.json();
      setIsLoading(false);
      setError(json.error);
      console.log(json.error, "JSON ERROR");
      localStorage.removeItem("user")
      console.log("We  don't have a user")
    }

    if (response.ok) {
      console.log("We have a user")
      const json = await response.json();
      
      // Store user data from the server response in local storage
      localStorage.setItem("user", JSON.stringify(json));

      // Update auth context
      dispatch({ type: "LOGIN", payload: json });

      // Fetch the user's role from another endpoint
      const roleResponse = await fetch("/api/user/role", {
        method: "GET",
        headers: { "Authorization": `Bearer ${json.token}` },
      });

      if (roleResponse.ok) {
        const roleData = await roleResponse.json();
        console.log(roleData, "role data")
        // Update user data with the role and store it in local storage
        const userWithRole = { ...json, role: roleData.role };
        localStorage.setItem("user", JSON.stringify(userWithRole));
        dispatch({ type: "LOGIN", payload: userWithRole });
      }

      const decodedToken = jwt.decode(json.token);
      if (decodedToken && decodedToken.exp * 1000 < Date.now()) {
        localStorage.removeItem("user");
        dispatch({ type: "LOGOUT" }); // Add a logout action to your context
        console.log("User token has expired. Removed from local storage and logged out.");
      }

      setIsLoading(false);
    }
    
  };

  return { login, isLoading, error };
};
