import React, { useEffect, useRef } from "react";
import Swiper from "swiper";
import "swiper/css";
import { Navigation, Pagination } from "swiper/modules";
import "swiper/css/navigation";
import "swiper/css/pagination";
import testimonialCircleOneLarge from "../images/testimonial-circle-1-large.png";
import testimonialCircleTwoLarge from "../images/testimonial-circle-2-large.png";
import testimonialCircleThreeLarge from "../images/testimonial-circle-3-large.png";

interface SliderProps {}

function Slider(props: SliderProps) {
  const swiperRef = useRef<Swiper | null>(null);

  useEffect(() => {
    swiperRef.current = new Swiper(".swiper", {
      modules: [Navigation, Pagination],
      breakpoints: {
        "@0.5": {
          slidesPerView: 1,
          spaceBetween: 20,
        },
        "@0.75": {
          slidesPerView: 2,
          spaceBetween: 20,
        },
        "@1.00": {
          slidesPerView: 3,
          spaceBetween: 40,
        },
        "@1.50": {
          slidesPerView: 3,
          spaceBetween: 50,
        },
      },
    });
  }, []);

  const handleCardHover = (index: number) => {
    // You can access and manipulate each card here based on the index
    // For example, you can update the card's state
    console.log(`Card ${index + 1} hovered`);
  };

  return (
    <div className="swiper">
      <div className="swiper-wrapper">
        {/* Card 1 */}
        <div
          className="swiper-card"
          onMouseEnter={() => handleCardHover(0)} // Call the function on hover
        >
          <img
            className="testimonial-img"
            src={testimonialCircleOneLarge}
            alt="testimonial-1-img-large"
          />
          <div className="name">
            <h4>Lance Hyland</h4>
            <p>Organic Farm Manager</p>
          </div>
          <div className="quote-circle">{/* SVG */}</div>
          <p className="text">
            “FlavourFinder ignited my culinary journey! Learning to cook online
            is a flavourful adventure.”
          </p>
        </div>

        {/* Card 2 */}
        <div
          className="swiper-card"
          onMouseEnter={() => handleCardHover(1)} // Call the function on hover
        >
          <img
            className="testimonial-img"
            src={testimonialCircleTwoLarge}
            alt="testimonial-2-img-large"
          />
          <div className="name">
            <h4>Shae Cox</h4>
            <p>Marketing Consultant</p>
          </div>
          <div className="quote-circle">{/* SVG */}</div>
          <p className="text">
            "FlavourFinder made me a kitchen pro. Their online cooking platform
            is practical and empowering!"
          </p>
        </div>

        {/* Card 3 */}
        <div
          className="swiper-card"
          onMouseEnter={() => handleCardHover(2)} // Call the function on hover
        >
          <img
            className="testimonial-img"
            src={testimonialCircleThreeLarge}
            alt="testimonial-3-img-large"
          />
          <div className="name">
            <h4>James Krever</h4>
            <p>Professional Chef</p>
          </div>
          <div className="quote-circle">{/* SVG */}</div>
          <p className="text">
            “FlavourFinder honed my kitchen techniques. Their digital cooking
            platform is efficient and engaging!”
          </p>
        </div>
      </div>
      <button className="curved-btn testimonials-btn">More Testimonials</button>
    </div>
  );
}

export default Slider;
