import React from "react";
import heroImg from "../images/hero-graphic.png";
import PlayIcon from "../svg/playIcon";
import secondaryHeroGraphic from "../images/secondary-hero-graphic.png";
import grapesDecoration from "../images/grapes.png";
import ScrollIndicator from "../svg/ScrollIndicator";
import DownArrow from "../svg/DownArrow";
import { useState } from "react";
function HomePage() {
  //home page states

  const [isScrollIndicatorHovered, setIsScrollIndicatorHovered] =
    useState(false);

  //animate hero section arrow

  // Function to handle the hover event on ScrollIndicator
  const handleScrollIndicatorHover = () => {
    setIsScrollIndicatorHovered(true);
  };

  // Function to handle the mouse leave event on ScrollIndicator
  const handleScrollIndicatorLeave = () => {
    setIsScrollIndicatorHovered(false);
  };

  return (
    <main>
      <section className="pbl-5">
        <div className="container">
          <img
            className="grapes-decoration pos-absolute"
            alt="grapes-decoration"
            src={grapesDecoration}
          ></img>
          <div className="even-columns">
            <div>
              <div className="flow">
                <h1>
                  Wake Up To The <span> Flavour Revolution</span>
                </h1>
                <p className="pbl-4">
                  Join a vibrant community of food enthusiasts on{" "}
                  <span> Flavour Finder, the recipe website that brings </span>{" "}
                  culinary creativity to life!
                </p>
                <div className="cta-buttons pb-5">
                  <button className="curved-btn ">Join Up</button>
                  <div className="play-btn-container">
                    <button className="play-btn">
                      <PlayIcon />
                    </button>
                    <p>How It Works</p>
                  </div>
                </div>
                <div className="display-f">
                  <div className="stats-hero-section mr-3">
                    <h3>50,000+</h3>
                    <p>Happy Users</p>
                  </div>
                  <img
                    alt="secondary-hero-graphic"
                    src={secondaryHeroGraphic}
                  ></img>
                </div>
              </div>
            </div>

            <div id="hero-graphic-column">
              {" "}
              <img src={heroImg} alt="hero-img"></img>
            </div>
          </div>
        </div>
        <div className="scroll-indicator-container">
          <ScrollIndicator
            onMouseEnter={handleScrollIndicatorHover}
            onMouseLeave={handleScrollIndicatorLeave}
          />
          <p>Scroll Down</p>
          <DownArrow   isHovered={isScrollIndicatorHovered}/>
        </div>
      </section>
    </main>
  );
}

export default HomePage;
