const resources = {
  ENG: {
    translation: {
      home: "Home",
      savedRecipesLAST: "Saved Recipes",
      friends: "Friends",
      logout: "Log Out",
    },
  },
  ESP: {
    translation: {
      home: "Inicio", // Added "Home" translation in Spanish
      savedRecipesLAST: "Recetas guardadas",
      friends: "Amigos",
      logout: "Cerrar sesión",
    },
  },
  CHN: {
    translation: {
      home: "主页", // Added "Home" translation in Mandarin (CHN)
      savedRecipesLAST: "已保存的食谱",
      friends: "朋友",
      logout: "登出",
    },
  },
};

// i18N Initialization
i18n
  .use(initReactI18next)
  .init({
    resources,
    keySeparator: false,
    interpolation: {
      escapeValue: false,
    },
  });

export default i18n;
