import React, { useState } from "react";
import { Outlet } from "react-router-dom";
import SideBarComponent from "../../components/SideBarComponent";
import RecipeSearchComponent from "../../components/RecipeSearchComponent";
import AvatarIcon from "../../images/user-icon.png";
import ProfileEditor from "../../components/ProfileEditor";

interface Ingredient {
  name: string;
  quantity: number;
  unit: string;
}

interface Recipe {
  img: string;
  _id: string;
  title: string;
  ingredients: Ingredient[];
}

function UserDashboardHome() {
  const [showProfileEditor, setShowProfilePictureChanger] = useState(false);

  return (
    <div className="dashboard">
   {showProfilePictureChanger ? <div className="dashboard-overlay"></div> : null}

      <SideBarComponent />
      <header className="dashboard-header">
        <RecipeSearchComponent />
        <div className="dashboard-buttons-container display-f">
          <img
            onClick={() => setShowProfilePictureChanger(true)}
            className="avatar"
            src={AvatarIcon}
            alt="user-icon"
          />
          <div className="notification-icon-container"></div>
        </div>
      </header>
      <Outlet />
      {showProfilePictureChanger && <ProfileEditor />}
    </div>
  );
}

export default UserDashboardHome;
