import React, { useRef, useState } from 'react';
import defaultUserPic from '../images/default-profile-pic.jpg';

interface ProfileEditorProps {
  showProfileEditor: boolean;
  setShowProfileEditor: (show: boolean) => void;
}

function ProfileEditor({ showProfileEditor, setShowProfileEditor }: ProfileEditorProps) {
  const fileInputRef = useRef<HTMLInputElement | null>(null);
  const [selectedImage, setSelectedImage] = useState<string | null>(null);

  const handleUploadClick = () => {
    fileInputRef?.current?.click();
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files && event.target.files[0];

    if (file) {
      // Read the selected file as a data URL
      const reader = new FileReader();

      reader.onload = (e) => {
        if (e.target) {
          setSelectedImage(e.target.result as string);
        }
      };

      reader.readAsDataURL(file);
    }
  };

  const handleCloseClick = () => {
    // Use the setShowProfileEditor callback to close the editor
    setShowProfileEditor(false);
  };

  return (
    <div className="profile-editor">
      <div className="display-f align-center">
        <svg
          style={{ cursor: 'pointer' }}
          onClick={handleCloseClick} // Use the handleCloseClick function
          width="15"
          height="15"
          viewBox="0 0 9 9"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M1 8.07L8.072 1M1 1L8.072 8.07"
            stroke="black"
            strokeWidth="1.5"
            strokeLinecap="round"
          />
        </svg>
        <h4>Change Profile Picture</h4>
        <button className="save-btn">Save</button>
      </div>
      <div className="upload-btn-wrapper">
        <div className="user-avatar">
          <button className="btn-upload-img" onClick={handleUploadClick}>
            {selectedImage ? (
              <img src={selectedImage} alt="selected-user-profile-picture" />
            ) : (
              <svg
                width="21"
                height="20"
                viewBox="0 0 21 20"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                {/* SVG path for your upload icon */}
              </svg>
            )}
          </button>
          <input
            type="file"
            name="myfile"
            ref={fileInputRef}
            style={{ display: 'none' }}
            accept="image/*" // Allow only image files
            onChange={handleFileChange} // Handle file selection
          />
        </div>
      </div>
    </div>
  );
}

export default ProfileEditor;
