import React, { ChangeEvent, FormEvent, useState } from 'react';

interface Ingredient {
  name: string;
  quantity: string;
  unit: string;
}

function AdminComponent() {
  // form states
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [ingredients, setIngredients] = useState<Ingredient[]>([{ name: '', quantity: '', unit: '' }]);
  const [recipeTitle, setRecipeTitle] = useState<string>('');
  const [cusisine, setCuisine] = useState<string>('');

  const uploadImage = async () => {
    if (!imageFile) {
      return null;
    }

    const formData = new FormData();
    formData.append("file", imageFile);
    formData.append("upload_preset", "RecipeAppUserPhotos");

    try {
      const response = await fetch("https://api.cloudinary.com/v1_1/deewhii9n/image/upload", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const uploadedImage = await response.json();
        const imageUrl = uploadedImage.secure_url;
        console.log("Image uploaded successfully. URL:", imageUrl);
        return imageUrl;
      } else {
        console.error(`Failed to upload image. Status: ${response.status}`);
        return null;
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      return null;
    }
  };

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setImageFile(event.target.files[0]);
    }
  };

  const handleIngredientChange = (index: number, key: keyof Ingredient, value: string) => {
    const updatedIngredients = [...ingredients];
    updatedIngredients[index][key] = value;
    setIngredients(updatedIngredients);
  };

  const handleAddIngredient = () => {
    setIngredients([...ingredients, { name: '', quantity: '', unit: '' }]);
  };

  const handleDeleteIngredient = (index: number) => {
    if (ingredients.length > 1) {
      const updatedIngredients = [...ingredients];
      updatedIngredients.splice(index, 1);
      setIngredients(updatedIngredients);
    }
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    const imageUrl = await uploadImage();

    if (imageUrl) {
      const recipeData = {
        title: recipeTitle,
        img: imageUrl,
        ingredients: ingredients,
        cuisine: cusisine
      };

      await createRecipe(recipeData);
      console.log('Recipe data posted:', recipeData);
    }
  };

  const createRecipe = async (recipeData: any) => {
    try {
      const response = await fetch("/api/recipes/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(recipeData),
      });

      if (response.ok) {
        console.log("Recipe created successfully:", recipeData);
      } else {
        console.error(`Failed to create recipe. Status: ${response.status}`);
      }
    } catch (error) {
      console.error("Error creating recipe:", error);
    }
  };

  return (
    <div className='admin'>
      <h2>Create New Recipe</h2>
      <form onSubmit={handleSubmit}>
        <input type='file' onChange={handleFileChange}></input>
        <div className='input-div'>
          <h4>Recipe</h4>
          <label htmlFor="title">Title</label>
          <input
            name='title'
            id='title'
            value={recipeTitle}
            onChange={(e) => setRecipeTitle(e.target.value)}
          ></input>
        </div>
        <div className='ingredients-container'>
          <h4>Ingredients</h4>
          {ingredients.map((ingredient, index) => (
            <div className='ingredient' key={index}>
              <div className="ingredient-name">
                <label htmlFor={`ingredient-name-${index}`}>Name</label>
                <input
                  type="text"
                  id={`ingredient-name-${index}`}
                  value={ingredient.name}
                  onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
                />
              </div>
              <div className="ingredient-quantity">
                <label htmlFor={`ingredient-quantity-${index}`}>Quantity</label>
                <input
                  type="text"
                  id={`ingredient-quantity-${index}`}
                  value={ingredient.quantity}
                  onChange={(e) => handleIngredientChange(index, 'quantity', e.target.value)}
                />
              </div>
              <div className="ingredient-unit">
                <label htmlFor={`ingredient-unit-${index}`}>Unit</label>
                <input
                  type="text"
                  id={`ingredient-unit-${index}`}
                  value={ingredient.unit}
                  onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                />
              </div>
              {ingredients.length > 1 && (
                <button
                  className='delete-ingredient-btn'
                  type="button"
                  onClick={() => handleDeleteIngredient(index)}
                >
          <svg className='minus-icon' width="8" height="" viewBox="0 0 8 2" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M0 0.75C0 0.551088 0.0790175 0.360322 0.21967 0.21967C0.360322 0.0790175 0.551088 0 0.75 0H7.25C7.44891 0 7.63968 0.0790175 7.78033 0.21967C7.92098 0.360322 8 0.551088 8 0.75C8 0.948912 7.92098 1.13968 7.78033 1.28033C7.63968 1.42098 7.44891 1.5 7.25 1.5H0.75C0.551088 1.5 0.360322 1.42098 0.21967 1.28033C0.0790175 1.13968 0 0.948912 0 0.75Z" fill="#fff"/>
</svg>

                </button>
              )}
            </div>
          ))}
          <div>
            <label htmlFor='cuisine'>Choose Cuisine</label>
            <select
              className='cuisine-select-menu'
              name='cuisine'
              value={cusisine}
              onChange={(e) => setCuisine(e.target.value)}
            >
              <option>Indian</option>
              <option>Spanish</option>
              <option>French</option>
              <option>Chinese</option>
              <option>Italian</option>
            </select>
          </div>
          <button className='Add-Ingredient-btn' type="button" onClick={handleAddIngredient}>
            Add Ingredient
          </button>
        </div>
        <button className='create-recipe-btn'>Create Recipe</button>
      </form>
    </div>
  );
}

export default AdminComponent;
