import React from "react";


interface ChevronProps {
  rotation: number;
  onClick: () => void;
}

//chevron states

const [showChevron, setShowChevron] = useState(false)

window.addEventListener("resize", () => {
  if (window.innerWidth > 1000) {
    // Your logic when the window width is above 1000px
    console.log("Window width is above 1000px");
  } else {
    // Your logic when the window width is 1000px or below
    console.log("Window width is 1000px or below");
  }
});

function Chevron({ rotation, onClick }: ChevronProps) {
  return (
    <svg
      className="footer-chevron"
      width="20"
      height="20"
      viewBox="0 0 20 20"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      style={{
        transition: "transform 0.3s",
        transform: `rotate(${rotation}deg)`,
        cursor: "pointer",
      }}
      onClick={onClick}
    >
      <path
        d="M10 13.75L3.75 7.5L4.625 6.625L10 12L15.375 6.625L16.25 7.5L10 13.75Z"
        fill="#fff"
      />
    </svg>
  );
}

export default Chevron;
