import React, { useEffect, useState } from "react";
import SideBarComponent from "../components/SideBarComponent";
import RecipeSearchComponent from "../components/RecipeSearchComponent";
import AvatarIcon from "../images/user-icon.png";

interface Ingredient {
  name: string;
  quantity: number;
  unit: string;
}

interface Recipe {
  img: string;
  _id: string;
  title: string;
  ingredients: Ingredient[];
}

function UserDashboard() {
  const [recipes, setRecipes] = useState<Recipe[] | null>(null);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch("/api/recipes");
        if (!response.ok) {
          console.error(`Failed to fetch recipes. Status: ${response.status}`);
          return;
        }
        const json = await response.json();
        setRecipes(json);
        console.log(json);
      } catch (error) {
        console.error("Error during fetch:", error);
      }
    };

    fetchRecipes();
  }, []);

  const saveUserRecipes = async (recipeId: string) => {
    try {
      // Fetch the whole document using the ID
      const response = await fetch(`/api/recipes/${recipeId}`);
      if (!response.ok) {
        console.error(
          `Failed to fetch recipe with ID ${recipeId}. Status: ${response.status}`
        );
        return;
      }
      const savedRecipe = await response.json();
      console.log("Saved Recipe:", savedRecipe);
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  return (
    <div className="dashboard">
      <SideBarComponent />
      <header className="dashboard-header">
        <RecipeSearchComponent />
        <div className="dashboard-buttons-container display-f">
          <img className="avatar" src={AvatarIcon} alt="user-icon" />
          <div className="notification-icon-container">{/* ... */}</div>
        </div>
      </header>

      <div className="user-recipes">
        {recipes &&
          recipes.map((recipe) => (
            <div key={recipe._id}>
              <img src={recipe.img} alt="food-img"></img>
              <p>{recipe.title}</p>
              {/* Pass the recipe ID to the saveUserRecipes function */}
              <button
                className="save-recipe-btn"
                onClick={() => saveUserRecipes(recipe._id)}
              >
                Save
              </button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default UserDashboard;
